function ExportFEN(switcher, boardPositions)
{
  var freeCellCount = 0;
  var FEN = "";
  for (var i = 7; i >= 0; i--)
  {
      for (var j = 0; j <= 7; j++)
      {
          var test = boardPositions[i*8+j];
          if (test == "Empty_Slot")
          {
              freeCellCount += 1;
          }
          else
          {
              var figure = boardPositions[i*8+j];
              if (freeCellCount != 0)
              {
                  FEN += String(freeCellCount);
                  freeCellCount = 0;
              }

              var figureName = figure.split('_');//HERE

              if (figureName[1] == "King")
              {
                  if (figureName[0] == "White")
                      FEN += "K";
                  else
                      FEN += "k";
              }
              else if (figureName[1] == "Queen")
              {
                  if (figureName[0] == "White")
                      FEN += "Q";
                  else
                      FEN += "q";
              }
              else if (figureName[1] == "Rook")
              {
                  if (figureName[0] == "White")
                      FEN += "R";
                  else
                      FEN += "r";
              }
              else if (figureName[1] == "Bishop")
              {
                  if (figureName[0] == "White")
                      FEN += "B";
                  else
                      FEN += "b";
              }
              else if (figureName[1] == "Knight")
              {
                  if (figureName[0] == "White")
                      FEN += "N";
                  else
                      FEN += "n";
              }
              else if (figureName[1] == "Pawn")
              {
                  if (figureName[0] == "White")
                      FEN += "P";
                  else
                      FEN += "p";
              }
          }
      }
      if (freeCellCount != 0)
      {
          FEN += String(freeCellCount);
      }
      freeCellCount = 0;
      if (i != 0)
          FEN += '/';
  }
  FEN += " ";
  var turnIndicator = global.get("currentPlayer");
  FEN += turnIndicator + " ";

  var castlingWhite = CheckCastling("White");
  var castlingBlack = CheckCastling("Black");
  if (!castlingWhite[0] && !castlingWhite[1] && !castlingBlack[0] && !castlingBlack[1])
  {
      FEN += "-";
  }
  else
  {
      if (castlingWhite[1] == true)
      {
          FEN += "K";
      }
      if (castlingWhite[0] == true)
      {
          FEN += "Q";
      }
      if (castlingBlack[1] == true)
      {
          FEN += "k";
      }
      if (castlingBlack[0] == true)
      {
          FEN += "q";
      }
  }
  FEN += " ";

  FEN += global.get("enPassant") + " ";

  FEN += String(global.get("halfMoveClock")) + " " + String(global.get("fullMoveClock"));

  return FEN;
}

