function GetDifference(previousSegments, currentSegments)
{
    var differences = [];

    for (var z = 0; z < previousSegments.length; z++)
    {
       var i = 0;
       var j = 0;
       var a = previousSegments[z];
       var b = currentSegments[z];
       for(var x=0; x < a.length; x++)
       {
            if(isCharNumber(a[x]))
            {
                a = SwapChar(a, "-", x, parseInt(a[x]));
            }
       }
       for(x = 0; x < b.length; x++)
       {
            if(isCharNumber(b[x]))
            {
                b = SwapChar(b, "-", x, parseInt(b[x]));
            }
       }        
        while (j < b.length)
        {
            if(a[i] != "-" || b[j] != "-")
            {
                if (a[i] != b[j] || i == a.length)
                {
                    if(a[i] != "-" && b[j] == "-")
                    {
                        differences.push([a[i], i, z, "previous"]);
                    }
                    else if(a[i] == "-" && b[j] != "-")
                    {
                        differences.push([b[j], j, z, "current"]);
                    }
                    else
                    {
                        differences.push([b[j], j, z, "current"]);
                    }
                }
            }
            i++;
            j++;
        }
    }
    if(differences.length != 0)
    {
        return differences;
    }
    else
   {
      return [];
   }
}

