function PrintBoard(boardPositions)
{
    var exportString = "OCELLUS 2\n";
    var tempString = "";
    for (var i = 7; i >= 0; i--)
    {
        for (var j = 0; j <= 7; j++)
        {
            if(j==0)
            {
                exportString += "| ";    
            }

            tempString = boardPositions[i*8+j].name;
            if (tempString == "Empty_Slot")
            {
                exportString += "------------";
            }
            else
            {
                if(tempString.split("_")[1] == "Queen")
                {
                    exportString += tempString + " ";
                }
                else if(tempString.split("_")[1] != "Knight" || tempString.split("_")[1] != "Bishop")
                {
                    exportString += " " + tempString + " ";
                }
            }
            exportString += " | ";
        }
        exportString += "\n";
    }
    return exportString;
}

if(msg.topic != "Dashboard")
{
    var data = msg.payload;

    var startingPosition = 0.0275;
    var tolerance = 0.015;
    var nextPosition = 0.055;

    var counterX = 0;
    var counterY = 0;

    if(data.dataflowevent){

        var boardPositions = new Array(64);
        var cameraPosition = data.dataflowevent.data.camerasMap[0][1].position;
        var objectArray = data.dataflowevent.data.itemsMap[0][1].dataList

        for(var k=0 ; k< objectArray.length;k++)
        {
            for(var i=-startingPosition ; i > -startingPosition-nextPosition * 7 ; i-=nextPosition)
            {
                for(var j=startingPosition; j < startingPosition+nextPosition * 7 ; j+=nextPosition)
                {
                    if(IsBetween(j,i,objectArray[k].position))
                    { 
                        boardPositions[counterY*8+counterX] = objectArray[k];
                    }
                    counterX++;
                }
                counterY++;
                counterX = 0;
            }
            counterX = 0;
            counterY = 0;
        }
        CreateEmptyFigures(boardPositions);
    }
    var newMsg = {payload : boardPositions, topic : "Chess_quadrants"};
    global.set("globalBoardPositions", boardPositions);
    return newMsg;
}

function CreateEmptyFigures(boardPositions)
{
    for(var i = 0; i < boardPositions.length; i++)
    {
        if(boardPositions[i] == undefined)
        {
            boardPositions[i] = {name:"Empty_Slot", position:{x : CalculateX(i), y : CalculateY(i), z : 0.04}};
        }
    }
}

function CalculateX(index)
{
    return ((index % 8) * nextPosition + startingPosition);
}

function CalculateY(index)
{
    return (-Math.floor( index / 8 ) * nextPosition - startingPosition);
}

function IsBetween(x, y, position) 
{
    if(position.x < x+tolerance && position.x > x-tolerance && position.y < y+tolerance && position.y > y-tolerance)
    {
        return true;
    }
    return false;
}

